from .models import Ayudantia
class adminAyudantia(admin.ModelAdmin):
	list_display = ["ayudante","aula"]
	list_filter=["ayudante"]
	search_fields=["ayudante", "aula"]                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
	class Meta:
		model= Ayudantia


admin.site.register(Ayudantia, adminAyudantia)