from django.contrib import admin

# Register your models here.
from .models import CDictada
class adminCDictada(admin.ModelAdmin):
	list_display = ["nombre","semana"]
	list_filter=["semana"]
	search_fields=["nombre", "semana"]                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
	class Meta:
		model= CDictada


admin.site.register(CDictada, adminCDictada)